// Helper function to get today's liturgy
export function getTodayLiturgy(): DailyLiturgy | undefined {
    const today = new Date().toISOString().split('T')[0];
    return dailyLiturgyData2026.find(day => day.date === today);
}

// Helper function to get liturgy by date
export function getLiturgyByDate(date: string): DailyLiturgy | undefined {
    return dailyLiturgyData2026.find(day => day.date === date);
}
